        /*
        * This method generates cp_objects.json file containing CheckPoint objects.
        * Further it creates archive containing cp_objects.json file and SmartConnector.py script.
        * isNeedGenOpt - if need create 2 file with optimized-based objects and comments (Cisco)
        * isCurrenctOptimized - if current json obj builds by opt package
        */
        public void CreateSmartConnector(bool isNeedGenOpt = false, bool isCurrentOptimized = false)
        {

            string[] pySmartConnectorFNs = new string[] {
                    "smartconnector.py"
                };

            bool isGeneratingSC = true;
            foreach (var pySmartConnectorFN in pySmartConnectorFNs)
            {
                if (!File.Exists(Directory.GetCurrentDirectory() + Path.DirectorySeparatorChar + "SmartConnector" + Path.DirectorySeparatorChar + pySmartConnectorFN))
                {
                    isGeneratingSC = false;
                    break;
                }
            }

            string compressorsDirPath = Directory.GetCurrentDirectory() + Path.DirectorySeparatorChar + "compressors";
            string compressorZip = Path.Combine(compressorsDirPath, "zip.exe");
            string compressorGtar = Path.Combine(compressorsDirPath, "gtar.exe");
            string compressorGzip = Path.Combine(compressorsDirPath, "gzip.exe");
            if (!File.Exists(compressorZip) || !File.Exists(compressorGtar) || !File.Exists(compressorGzip))
                isGeneratingSC = false;

            if (isGeneratingSC)
            {
                if (IsConsoleRunning)
                {
                    Console.WriteLine("Generating Smart Connector ...");
                    Progress.SetProgress(90);
                    Thread.Sleep(1000);
                }
                RaiseConversionProgress(90, "Generating Smart Connector ...");
                string cpObjectsJsonFN = !isCurrentOptimized ? regularNameJson : optimizedNameJson;
                string cpObjectsJsonPath = _targetFolder + Path.DirectorySeparatorChar;

                #region adding objects and rules to list for generating JSON

                List<CheckPointObject> cpJsonObjects = new List<CheckPointObject>();
                cpJsonObjects.AddRange(_cpDomains);
                cpJsonObjects.AddRange(_cpHosts);
                cpJsonObjects.AddRange(_cpNetworks);
                cpJsonObjects.AddRange(_cpRanges);
                // adding NetworkGroups and NetworkGroups with Exclusions
                CheckPoint_NetworkGroup allInternal = null;
                bool splitNetworkGroupsCreation = (_cpNetworkGroups.Count > 0 && _cpGroupsWithExclusion.Count > 0);
                if (_cpNetworkGroups.Count > 0)
                {
                    foreach (CheckPoint_NetworkGroup obj in _cpNetworkGroups)
                    {
                        if (obj.Name == AllInternalNetwotkGroupName)
                        {
                            allInternal = obj;
                            continue;
                        }
                        if (splitNetworkGroupsCreation && obj.CreateAfterGroupsWithExclusion)
                        {
                            continue;
                        }

                        cpJsonObjects.Add(obj);
                    }
                }
                if (_cpGroupsWithExclusion.Count > 0)
                {
                    foreach (CheckPoint_GroupWithExclusion obj in _cpGroupsWithExclusion)
                    {
                        cpJsonObjects.Add(obj);
                    }
                }
                if (splitNetworkGroupsCreation)
                {
                    foreach (CheckPoint_NetworkGroup obj in _cpNetworkGroups)
                    {
                        if (!obj.CreateAfterGroupsWithExclusion)
                        {
                            continue;
                        }
                        cpJsonObjects.Add(obj);
                    }
                }
                if (allInternal != null)
                {
                    cpJsonObjects.Add(allInternal);
                }
                // NetworkGroups and NetworkGroups with Exclusion are added
                cpJsonObjects.Add(_cpSimpleGateway);
                cpJsonObjects.AddRange(_cpZones);
                cpJsonObjects.AddRange(_cpTcpServices);
                cpJsonObjects.AddRange(_cpUdpServices);
                cpJsonObjects.AddRange(_cpOtherServices);
                cpJsonObjects.AddRange(_cpServiceGroups);
                cpJsonObjects.AddRange(_cpTimeGroups);
                cpJsonObjects.AddRange(_cpTimes);
                // objects are added
                // adding Security rules
                if (!isCurrentOptimized)
                    cpJsonObjects.Add(_cpPackages.FirstOrDefault());
                else
                    cpJsonObjects.Add(_cpPackages.ElementAtOrDefault(1));
                // adding NAT rules
                if (!isCurrentOptimized)
                    _cpNatRules.ForEach(x => x.Package = _cpPackages[0].Name);
                else
                    _cpNatRules.ForEach(x => x.Package = _cpPackages[1].Name);
                cpJsonObjects.AddRange(_cpNatRules);

                //remove all NULL elements
                cpJsonObjects.RemoveAll(x => x == null);
                #endregion

                if (cpJsonObjects.Count > 250000)
                {
                    File.WriteAllText(cpObjectsJsonPath + cpObjectsJsonFN, JsonConvert.SerializeObject(cpJsonObjects[0], Formatting.Indented));

                    for (var i = 1; i < cpJsonObjects.Count;)
                    {
                        var dest = cpJsonObjects.Skip(i).Take(250000).ToArray();
                        i += 250000;
                        File.AppendAllText(cpObjectsJsonPath + cpObjectsJsonFN, JsonConvert.SerializeObject(dest, Formatting.Indented));
                    }
                }
                else File.WriteAllText(cpObjectsJsonPath + cpObjectsJsonFN, JsonConvert.SerializeObject(cpJsonObjects, Formatting.Indented));

                string smartConnectorArchiveName = "smartconnector_" + _vendorFileName;
                string smartConnectorArchivePath = _targetFolder + Path.DirectorySeparatorChar + smartConnectorArchiveName;

                //if we need two json files and currenct is not optimized then it means what next file will be optimized and then can be created zip
                if (isNeedGenOpt && !isCurrentOptimized)
                    return;

                CreateZip(compressorsDirPath, pySmartConnectorFNs, smartConnectorArchivePath, smartConnectorArchiveName, cpObjectsJsonPath, isNeedGenOpt);
            }
        }

        protected void PostProcessNatRule64(CheckPoint_NAT_Rule natRule)
        {
            var isSourceHost = false;
            var isSourceNetwork = false;
            var isSourceIpv6 = false;
            var source = natRule?.Source != null ? natRule?.Source : natRule?.Destination;
            if (source != null)
            {
                isSourceHost = source is CheckPoint_Host;
                isSourceNetwork = source is CheckPoint_Network;
                if (isSourceHost)
                {
                    var host = (CheckPoint_Host)source;
                    isSourceIpv6 = NetworkUtils.IsValidIpv6(host.IpAddress);
                }
                if (isSourceNetwork)
                {
                    var network = (CheckPoint_Network)source;
                    isSourceIpv6 = NetworkUtils.IsValidIpv6(network.Subnet);
                }
            }
            if (!isSourceIpv6)
            {
                // destination is not Ipv6, no sense to continue
                return;
            }

            var isTranslatedSourceHost = false;
            var isTranslatedSourceNetwork = false;
            var isTranslatedSourceIpv4 = false;
            if (natRule?.TranslatedSource != null)
            {
                isTranslatedSourceHost = natRule.TranslatedSource is CheckPoint_Host;
                isTranslatedSourceNetwork = natRule.TranslatedSource is CheckPoint_Network;
                if (isTranslatedSourceHost)
                {
                    var host = (CheckPoint_Host)natRule.TranslatedSource;
                    isTranslatedSourceIpv4 = NetworkUtils.IsValidIpv4(host.IpAddress);
                }
                if (isTranslatedSourceNetwork)
                {
                    var network = (CheckPoint_Network)natRule.TranslatedSource;
                    isTranslatedSourceIpv4 = NetworkUtils.IsValidIpv4(network.Subnet);
                }
            }
            if (!isTranslatedSourceIpv4)
            {
                // translated source is not Ipv4, no sense to continue
                return;
            }

            // change NAT rule method
            natRule.Method = CheckPoint_NAT_Rule.NatMethod.Nat64;

            //  a address-range should be used instead of network of host for translated source
            IPRange ipRange;
            if (isTranslatedSourceNetwork)
            {
                var network = (CheckPoint_Network)natRule.TranslatedSource;
                ipRange = network.GetIPRanges().Ranges[0];
            }
            else // if translated source is a host
            {
                var host = (CheckPoint_Host)natRule.TranslatedSource;
                ipRange = host.GetIPRanges().Ranges[0];
            }
            var checkpointRange = new CheckPoint_Range();
            checkpointRange.RangeFrom = NetworkUtils.Number2Ip(ipRange.Minimum);
            checkpointRange.RangeTo = NetworkUtils.Number2Ip(ipRange.Maximum);
            checkpointRange.Name = "r_" + checkpointRange.RangeFrom + "-" + checkpointRange.RangeTo;
            if (_cpObjects.GetObject(checkpointRange.Name) == null)
            {
                AddCheckPointObject(checkpointRange);
            }
            natRule.TranslatedSource = checkpointRange;
        }

